import React, { useState } from 'react'
import { Jumbotron, Container, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap'

function Cadastrar() {

   const [meta, setMeta] = useState({
      name: '',
      description: '',
      status: ''
   })

   const [response, setResponse] = useState({
      formSave: false,
      type: '',
      message: ''
   })

   //pega o que já estava anteriormente preenchido e atualiza. Utiliza o name e o value do input
   const onChangeInput = e => setMeta({ ...meta, [e.target.name]: e.target.value })

   const sendMeta = async e => {
      e.preventDefault()

      setResponse({ formSave: true })

      try {
         const res = await fetch('http://localhost:8080/metas', {
            method: 'POST',
            body: JSON.stringify(meta),
            headers: {
               'Content-type': 'application/json'
            }
         })

         const responseEnv = await res.json()

         if (responseEnv.error) {
            setResponse({
               formSave: false,
               type: 'error',
               message: responseEnv.message
            })
         } else {
            setResponse({
               formSave: false,
               type: 'success',
               message: responseEnv.message
            })
         }
      } catch (err) {
         setResponse({
            formSave: false,
            type: 'error',
            message: 'ERROR - Não foi possível conectar a API. Meta não cadastrada!'
         })
      }
   }

   return (
      <>
         <Jumbotron fluid className="form">
            <style >
               {`.form{
                  padding-top: 30px;
                  padding-bottom: 150px;
                  margin-bottom: 0 !important;
               }`}
            </style>
            <Container>
               <h1 className="display-4 text-center">Cadastrar Meta</h1>
               <hr />
               <Form onSubmit={sendMeta}>
                  <FormGroup>
                     <Label for="name">Nome</Label>
                     <Input 
                        type="text" 
                        name="name"
                        id="name"
                        placeholder="Nome da meta"
                        onChange={onChangeInput}>
                     </Input> 
                  </FormGroup>
                  <br /><br />
                  <FormGroup>
                     <Label for="description">Descrição</Label>
                     <Input 
                        type="text" 
                        name="description"
                        id="description"
                        placeholder="Descrição da meta"
                        onChange={onChangeInput}>
                     </Input> 
                  </FormGroup>



                  <label>Descrição: </label>
                  <input
                     type="text"
                     name="description"
                     id="description"
                     placeholder="Descrição da meta"
                     onChange={onChangeInput}
                  />
                  <br /><br />
                  <label>Status: </label>
                  <input
                     type="text"
                     name="status"
                     id="status"
                     placeholder="Status da meta"
                     onChange={onChangeInput}
                  />
                  <br /><br />
                  {/* bloqueando o botão até que a API responda. Para evitar reenvios do formulário em caso de demora na resposata */}
                  {response.formSave ? <button type="submit" disabled>Enviando...</button> : <button type="submit">Cadastrar</button>}

                  {/* mensagens de sucesso ou de erro o gravar a meta */}
                  {response.type === 'error' ? <p>{response.message}</p> : ""}
                  {response.type === 'success' ? <p>{response.message}</p> : ""}

                  </Form>
            </Container>
         </Jumbotron>
      </>
   )
}

export default Cadastrar
